import React, { useState } from "react";
import NavBarComp from "../../components/homepage/NavBarComp";
import Button from "react-bootstrap/Button";
import FloatingLabel from "react-bootstrap/FloatingLabel";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import "../../styles/pageStyles/testDashStyle.css";
import MakeNewKeysAPI from "../../api/MakeNewKeysAPI.jsx";
export default props => {
    const [keyPair, setKeyPair] = useState({'public':'','private':''})

    const generateNewKeys = (res) => {
        setKeyPair(res);
        console.log("this is the response: ", res)
    }
    const handleUpdate = e => {
        e.preventDefault();
    }

    return (
        <>
            <div className="testdashwrap">
                <div className="testdashtopfill">
                    <h1>Welcome, guest!</h1>
                </div>

                <NavBarComp />
                <div className="testusercard">
                    <h2>[Username] [email]</h2>
                    <Form onSubmit={handleUpdate}>
                        <FloatingLabel controlId="username" label="Username" className="mb-3">
                            <Form.Control type="text" placeholder="Username" />
                        </FloatingLabel>
                        <FloatingLabel
                            controlId="floatingInput"
                            label="Email address"
                            className="mb-3">
                            <Form.Control type="email" placeholder="name@example.com" />
                        </FloatingLabel>
                        <Button>Save Changes</Button>
                    </Form>
                </div>
                <div className="addresses">
                    <h2>Here's your tesnet key pair:</h2>
                    {/* public */}
                    <h6>public: </h6>
                    {keyPair.public.length === 0 ?
                        <p>&nbsp;</p>
                        : 
                        <p>{keyPair.public}</p>}
                    {/* private */}
                    <h6>private: </h6>
                    {keyPair.private.length === 0 ?
                        <p>&nbsp;</p>
                        : <p>{keyPair.private}</p>}
                    <MakeNewKeysAPI response={generateNewKeys} className="newkeysplz"/>
                </div>


            </div>
        </>
    )
}
