import Button from "react-bootstrap/Button";
import axios from "axios";
import { Networks } from "stellar-sdk";
const TestnetTransactAPI = props => {
    const StellarSdk = require("stellar-sdk");
    // destination account, in this case the user
    const { reciever } = props;
    // source account
    const srcPubKey = 'GBSN5LZY5BMBJ5J3QWRYY7WAOYG3DVRCK76PN4MR3E7GBQBINGO3SGTV';
    const srcSecKey = 'SD7MGXUGVAQVJVKCBZSK4GHGRQYS67X4F4KU6T7P7EAVP7Y3CMCDZTTN';
    const srcKeyPair = StellarSdk.Keypair.fromSecret(srcSecKey);
    const amnt = '100.000';
    // instantiate horizon instance from stellar.org. the live network is "horizon.stellar.org"
    const server = new StellarSdk.Server("https://horizon-testnet.stellar.org");

    // || these are deprecated, refer to: https://git.io/fj9fG
    // StellarSdk.Network.usePublicNetwork();
    // StellarSdk.Network.useTestNetwork();

    // transactions require valid 'sequence number' from account
    const account = axios.get(server.loadAccount(srcPubKey));

    // fetch base fee
    const fee = StellarSdk.BASE_FEE;

    const handleClick = e => {


        const transaction = new StellarSdk.TransactionBuilder(
            account, { fee, networkPassphrase: Networks.TESTNET})
            // transactions can have many operations...
            // add a 'payment' operation
            .addOperation(StellarSdk.Operation.payment({
                destination: reciever,
                // need to specify 'native' so we can send Lumens
                asset: StellarSdk.Asset.native(),
                // Lumens are divisible by 7 decimal places. ex: 100.1234567
                amount: amnt,
            }))
            // transation is valid for only 30 seconds
            .setTimout(30)
            // to add memo:
            // .addMemo(StellarSdk.Memo.text('Hello World!'))
            .build();

        // source must sign the transaction with their keypair
        transaction.sign(srcKeyPair);

        console.log(transaction.toEnvelope().toXDR('base64'));

        // submit transaction to Horizon server
        // try {
        //     const transactionRes = await server.submitTransation(transaction);
        //     console.log(JSON.stringify(transactionRes, null, 2));
        //     console.log('\nSuccess! View the transaction at: ');
        //     console.log(transactionResult._links.transaction.href);
        // } catch (err) {
        //     console.log('An error has occured');
        //     console.log(err);
        // }
        const transactionRes = axios.get(server.submitTransation(transaction))
            .then(res => {
                console.log('\nSuccess! View the transaction at: ');
                console.log(transactionRes._links.transaction.href);
                console.log(res.data, null, 2);
            })
            .catch(err => console.log('An error has occurred', err))
    }

    return (<>
        <Button onClick={handleClick}>Confirm</Button>
    </>)
}
export default TestnetTransactAPI;
