import axios from "axios";
import StellarSdk from "stellar-sdk";
import Button from "react-bootstrap/Button"
import { useState } from "react";
export default props => {
    const [keyPair,setKeyPair] = useState({'public':'','private':''})
    const { response } = props;
    const server = new StellarSdk.Server("https://horizon-testnet.stellar.org");

    // pair.secret();
    // pair.publicKey();
    
    // console.log(pair.canSign());
    // console.log('public: ')
    // console.log(pair.publicKey());
    // console.log('secret: ')
    // console.log(pair.secret());
    const request = () => {
        const pair = StellarSdk.Keypair.random();
        console.log("public key: ",pair.publicKey());
        console.log("private key: ",pair.privateKey());
        axios.get(`https://friendbot.stellar.org?addr=${encodeURIComponent(
            pair.publicKey(),
        )}`)
            .then(res => {
                setKeyPair({'public':pair.publicKey(),'private':pair.secret()})
                console.log("SUCCESS! You have a new account :)\n", res.data)
            })
            .catch(err => console.log("error while creating account, ", err))
    }
    const getTransactions = pubKey => {
        server.transactions()
            .forAccount(pubKey)
            .call().then(function (r) { console.log(r); });
    }
    const handleClick = () => {
        request();
        response(keyPair);
        // getTransactions();
    }

    return <Button onClick={handleClick}>Make New Keys</Button>


}
